package week1.day2;

public class PrimeNumber {
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=29;
		for (int i=2; i<n;i++)
		{
			if (n%i == 0)
			{
				System.out.println("It is not a prime");
			break;
			}
			
			else {
				System.out.println("It is prime");
			break;
			}
			
		}
		
		
		
		
		
		}}
			
			
		
	


